import { ref, reactive, onMounted } from 'vue';
import { ElMessage, ElMessageBox } from 'element-plus';
import { Search, Plus, Edit, Delete, ArrowDown } from '@element-plus/icons-vue';
import request from '@/utils/request';
import { formatDate } from '@/utils/format';

// Search form

export default {
  __name: 'UserManage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchForm = reactive({
      keyword: '',
      role: '',
      status: ''
    });

    // Table data
    const loading = ref(false);
    const userList = ref([]);
    const total = ref(0);
    const currentPage = ref(1);
    const pageSize = ref(10);

    // Dialog related
    const dialogVisible = ref(false);
    const dialogType = ref('add');
    const userFormRef = ref(null);
    const userForm = reactive({
      name: '',
      email: '',
      password: '',
      roleValue: 2,
      statusValue: 1,
      avatar: ''
    });

    // Form validation rules
    const userRules = {
      name: [{
        required: true,
        message: 'Please enter username',
        trigger: 'blur'
      }, {
        min: 2,
        max: 20,
        message: 'Length should be between 2 and 20 characters',
        trigger: 'blur'
      }],
      email: [{
        required: true,
        message: 'Please enter email address',
        trigger: 'blur'
      }, {
        type: 'email',
        message: 'Please enter a valid email address',
        trigger: 'blur'
      }],
      password: [{
        required: true,
        message: 'Please enter password',
        trigger: 'blur'
      }, {
        min: 6,
        message: 'Password length cannot be less than 6 characters',
        trigger: 'blur'
      }],
      roleValue: [{
        required: true,
        message: 'Please select a role',
        trigger: 'change'
      }]
    };

    // Get user list
    const fetchUserList = async () => {
      loading.value = true;
      try {
        const params = {
          page: currentPage.value,
          pageSize: pageSize.value,
          ...searchForm
        };
        const res = await request({
          url: '/admin/users',
          method: 'get',
          params
        });
        userList.value = res.data.list;
        total.value = res.data.total;
      } catch (error) {
        console.error('Failed to fetch user list:', error);
      } finally {
        loading.value = false;
      }
    };

    // Search
    const handleSearch = () => {
      currentPage.value = 1;
      fetchUserList();
    };

    // Add user
    const handleAdd = () => {
      dialogType.value = 'add';
      Object.assign(userForm, {
        name: '',
        email: '',
        password: '',
        roleValue: 2,
        statusValue: 1,
        avatar: ''
      });
      dialogVisible.value = true;
    };

    // Edit user
    const handleEdit = row => {
      dialogType.value = 'edit';
      Object.assign(userForm, row);
      dialogVisible.value = true;
    };

    // Delete user
    const handleDelete = row => {
      ElMessageBox.confirm('Are you sure you want to delete this user?', 'Warning', {
        confirmButtonText: 'Confirm',
        cancelButtonText: 'Cancel',
        type: 'warning'
      }).then(async () => {
        try {
          await request({
            url: `/admin/users/${row.userId}`,
            method: 'delete'
          });
          ElMessage.success('Deleted successfully');
          fetchUserList();
        } catch (error) {
          console.error('Failed to delete user:', error);
          ElMessage.error('Failed to delete user');
        }
      }).catch(error => {
        // Handle when user clicks cancel button
        if (error === 'cancel') {
          return;
        }
        console.error('Operation cancelled:', error);
      });
    };

    // Update user status
    const handleStatusChange = async row => {
      try {
        await request({
          url: `/admin/users/${row.userId}/status`,
          method: 'put',
          params: {
            status: row.statusValue
          }
        });
        ElMessage.success('Status updated successfully');
      } catch (error) {
        console.error('Failed to update status:', error);
        row.statusValue = row.statusValue === 1 ? 0 : 1; // Restore original status
      }
    };

    // Add file upload related reference
    const fileInput = ref(null);

    // Add error state variables
    const nameError = ref('');
    const emailError = ref('');
    const passwordError = ref('');

    // Trigger file selection
    const triggerFileInput = () => {
      fileInput.value.click();
    };

    // Handle file selection change
    const handleFileChange = e => {
      const file = e.target.files[0];
      if (!file) return;

      // Validate file type and size
      const isValidImageType = /^image\/(jpeg|png|gif|webp|bmp|svg\+xml)$/.test(file.type);
      const isLt2M = file.size / 1024 / 1024 < 2;
      if (!isValidImageType) {
        ElMessage.error('Please upload a valid image format (JPG, PNG, GIF, WEBP, BMP, SVG)');
        return;
      }
      if (!isLt2M) {
        ElMessage.error('Image size cannot exceed 2MB');
        return;
      }

      // Create a FormData object
      const formData = new FormData();
      formData.append('file', file);

      // Upload file
      request({
        url: '/files/images',
        method: 'post',
        data: formData,
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(res => {
        userForm.avatar = res.data;
        ElMessage.success('Avatar uploaded successfully');
      }).catch(() => {
        ElMessage.error('Failed to upload avatar');
      });
    };

    // Validate and submit form
    const validateAndSubmit = () => {
      // Reset error messages
      nameError.value = '';
      emailError.value = '';
      passwordError.value = '';
      let isValid = true;

      // Validate username
      if (!userForm.name) {
        nameError.value = 'Please enter username';
        isValid = false;
      } else if (userForm.name.length < 2 || userForm.name.length > 20) {
        nameError.value = 'Username length should be between 2-20 characters';
        isValid = false;
      }

      // Validate email
      const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!userForm.email) {
        emailError.value = 'Please enter email address';
        isValid = false;
      } else if (!emailPattern.test(userForm.email)) {
        emailError.value = 'Please enter a valid email address';
        isValid = false;
      }

      // Validate password (only in add mode)
      if (dialogType.value === 'add') {
        if (!userForm.password) {
          passwordError.value = 'Please enter password';
          isValid = false;
        } else if (userForm.password.length < 6) {
          passwordError.value = 'Password length cannot be less than 6 characters';
          isValid = false;
        }
      }

      // If validation passes, submit form
      if (isValid) {
        if (dialogType.value === 'add') {
          addUser();
        } else {
          updateUser();
        }
      }
    };

    // Add user
    const addUser = async () => {
      try {
        await request({
          url: '/admin/users',
          method: 'post',
          data: userForm
        });
        ElMessage.success('Added successfully');
        dialogVisible.value = false;
        fetchUserList();
      } catch (error) {
        console.error('Failed to add user:', error);
      }
    };

    // Update user
    const updateUser = async () => {
      try {
        await request({
          url: `/admin/users/${userForm.userId}`,
          method: 'put',
          data: userForm
        });
        ElMessage.success('Updated successfully');
        dialogVisible.value = false;
        fetchUserList();
      } catch (error) {
        console.error('Failed to update user:', error);
      }
    };

    // Pagination related
    const handleSizeChange = val => {
      pageSize.value = val;
      fetchUserList();
    };
    const handleCurrentChange = val => {
      currentPage.value = val;
      fetchUserList();
    };

    // Get role tag type
    const getRoleType = roleValue => {
      const typeMap = {
        0: 'danger',
        1: 'warning',
        2: 'success',
        3: 'info'
      };
      return typeMap[roleValue] || 'info';
    };

    // Get role name
    const getRoleName = roleValue => {
      const nameMap = {
        0: 'Admin',
        1: 'Restaurant Manager',
        2: 'User',
        3: 'Staff'
      };
      return nameMap[roleValue] || 'Unknown Role';
    };

    // Initialize
    onMounted(() => {
      fetchUserList();
    });
    const __returned__ = {
      searchForm,
      loading,
      userList,
      total,
      currentPage,
      pageSize,
      dialogVisible,
      dialogType,
      userFormRef,
      userForm,
      userRules,
      fetchUserList,
      handleSearch,
      handleAdd,
      handleEdit,
      handleDelete,
      handleStatusChange,
      fileInput,
      nameError,
      emailError,
      passwordError,
      triggerFileInput,
      handleFileChange,
      validateAndSubmit,
      addUser,
      updateUser,
      handleSizeChange,
      handleCurrentChange,
      getRoleType,
      getRoleName,
      ref,
      reactive,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get Search() {
        return Search;
      },
      get Plus() {
        return Plus;
      },
      get Edit() {
        return Edit;
      },
      get Delete() {
        return Delete;
      },
      get ArrowDown() {
        return ArrowDown;
      },
      get request() {
        return request;
      },
      get formatDate() {
        return formatDate;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
