import { ref, reactive, onMounted } from 'vue';
import request from '@/utils/request';
import { useRouter } from 'vue-router';
const defaultAvatar = 'https://cube.elemecdn.com/3/7c/3ea6beec64369c2642b92c6726f1epng.png';

// User Information

export default {
  __name: 'Profile',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();

    // Back to Home
    const goBack = () => {
      router.push('/');
    };

    // Default Avatar
    const userInfo = ref({
      userId: '',
      name: '',
      email: '',
      role: '',
      avatar: '',
      createdAt: '',
      status: ''
    });

    // Password Form
    const passwordForm = reactive({
      oldPassword: '',
      newPassword: '',
      confirmPassword: ''
    });

    // Edit Form State
    const showEditDialog = ref(false);
    const showPasswordFields = ref(false);
    const editForm = reactive({
      name: '',
      avatar: '',
      oldPassword: '',
      newPassword: '',
      confirmPassword: ''
    });

    // State Control
    const showPasswordDialog = ref(false);
    const isSubmitting = ref(false);
    const showPassword = reactive({
      old: false,
      new: false,
      confirm: false
    });

    // File Upload Related
    const fileInput = ref(null);
    const triggerFileInput = () => {
      fileInput.value.click();
    };

    // Get User Information
    const fetchUserInfo = async () => {
      try {
        const response = await request.get('/users/info');
        if (response.code === 200) {
          userInfo.value = response.data;
        }
      } catch (error) {
        console.error('Failed to get user information:', error);
      }
    };

    // Format Date
    const formatDate = date => {
      return new Date(date).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    };

    // Show Password Change Dialog
    const showPasswordModal = () => {
      showPasswordDialog.value = true;
    };

    // Close Password Change Dialog
    const closePasswordDialog = () => {
      showPasswordDialog.value = false;
      passwordForm.oldPassword = '';
      passwordForm.newPassword = '';
      passwordForm.confirmPassword = '';
    };

    // Toggle Password Display State
    const togglePassword = field => {
      showPassword[field] = !showPassword[field];
    };

    // Handle Password Change
    const handlePasswordChange = async () => {
      if (!passwordForm.oldPassword || !passwordForm.newPassword || !passwordForm.confirmPassword) {
        alert('Please fill in all information');
        return;
      }
      if (passwordForm.newPassword !== passwordForm.confirmPassword) {
        alert('The two passwords do not match');
        return;
      }
      isSubmitting.value = true;
      try {
        const response = await request.put('/users/update', {
          oldPassword: passwordForm.oldPassword,
          newPassword: passwordForm.newPassword,
          confirmPassword: passwordForm.confirmPassword
        });
        if (response.code === 200) {
          alert('Password change successful');
          closePasswordDialog();
        }
      } catch (error) {
        console.error('Failed to change password:', error);
        alert('Failed to change password');
      } finally {
        isSubmitting.value = false;
      }
    };

    // Handle Avatar Upload
    const handleAvatarUpload = () => {
      showEditDialog.value = true;
      setTimeout(() => {
        triggerFileInput();
      }, 300);
    };

    // Handle File Selection
    const handleFileChange = async event => {
      const file = event.target.files[0];
      if (!file) return;
      const formData = new FormData();
      formData.append('file', file);
      try {
        const response = await request.post('/files/images', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.code === 200) {
          editForm.avatar = response.data.url || response.data;
        }
      } catch (error) {
        console.error('Failed to upload avatar:', error);
        alert('Failed to upload avatar, please try again later');
      }
    };

    // Start Editing Profile
    const startEdit = () => {
      editForm.name = userInfo.value.name;
      editForm.avatar = userInfo.value.avatar;
      showEditDialog.value = true;
    };

    // Close Edit Dialog
    const closeEditDialog = () => {
      showEditDialog.value = false;
      showPasswordFields.value = false;
      editForm.name = '';
      editForm.avatar = '';
      editForm.oldPassword = '';
      editForm.newPassword = '';
      editForm.confirmPassword = '';
    };

    // Toggle Password Field Display
    const togglePasswordFields = () => {
      showPasswordFields.value = !showPasswordFields.value;
      if (!showPasswordFields.value) {
        editForm.oldPassword = '';
        editForm.newPassword = '';
        editForm.confirmPassword = '';
      }
    };

    // Handle Profile Update
    const handleProfileUpdate = async () => {
      if (!editForm.name.trim()) {
        alert('Please enter a username');
        return;
      }
      if (showPasswordFields.value) {
        if (!editForm.oldPassword || !editForm.newPassword || !editForm.confirmPassword) {
          alert('Please fill in all password information');
          return;
        }
        if (editForm.newPassword !== editForm.confirmPassword) {
          alert('The two passwords do not match');
          return;
        }
      }
      isSubmitting.value = true;
      try {
        const updateData = {
          name: editForm.name,
          avatar: editForm.avatar
        };
        if (showPasswordFields.value) {
          Object.assign(updateData, {
            oldPassword: editForm.oldPassword,
            newPassword: editForm.newPassword,
            confirmPassword: editForm.confirmPassword
          });
        }
        const response = await request.put('/users/update', updateData);
        if (response.code === 200) {
          userInfo.value = response.data;
          alert('Update successful');
          closeEditDialog();
        }
      } catch (error) {
        console.error('Failed to update:', error);
        alert('Failed to update, please try again later');
      } finally {
        isSubmitting.value = false;
      }
    };

    // Component Mounted Get User Information
    onMounted(() => {
      fetchUserInfo();
    });
    const __returned__ = {
      router,
      goBack,
      defaultAvatar,
      userInfo,
      passwordForm,
      showEditDialog,
      showPasswordFields,
      editForm,
      showPasswordDialog,
      isSubmitting,
      showPassword,
      fileInput,
      triggerFileInput,
      fetchUserInfo,
      formatDate,
      showPasswordModal,
      closePasswordDialog,
      togglePassword,
      handlePasswordChange,
      handleAvatarUpload,
      handleFileChange,
      startEdit,
      closeEditDialog,
      togglePasswordFields,
      handleProfileUpdate,
      ref,
      reactive,
      onMounted,
      get request() {
        return request;
      },
      get useRouter() {
        return useRouter;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
